mapper.update:
UPDATE freeBoard_reply
SET rDepth = rDepth + 1
WHERE bId = #{bId} AND rGroup = #{rGroup} AND rDepth >= #{rDepth}

UPDATE freeBoard_reply
SET rWriter = #{rWriter}, rContent = #{rContent}
WHERE bId = #{bId} AND rId = #{rId} AND rPassword = #{rPassword}

UPDATE freeBoard_reply
SET rDelete = 1
WHERE bId = #{bId} AND rId = #{rId} AND rPassword = #{rPassword}
